function translatePigLatin(str) {

let regex = /[aeiou]/;
console.log(str.search(regex));
//for words that start with vowwls
if(regex.test(str[0])){
  console.log(str+"way");
  return str+"way";
}else if(str.search(regex)<0){
	return str+"ay";
}
let removed = str.slice(0, str.search(regex));
console.log(removed);
let remaining = str.slice(str.search(regex));
console.log(remaining);


console.log(remaining+removed+"ay");
return remaining+removed+"ay";
}


